import React, { useState, useContext } from "react";

import "./Authentication.css";

import Input from "../../shared/components/FormElements/Input";
import Button from "../../shared/components/FormElements/Button";
import Card from "../../shared/components/UIElement/Card";
import ErrorModal from "../../shared/components/UIElement/ErrorModal";
import LoadingSpinner from "../../shared/components/UIElement/LoadingSpinner";

import {
  VALIDATOR_EMAIL,
  VALIDATOR_MINLENGTH,
  VALIDATOR_MAXLENGTH,
} from "../../shared/utils/validators";

import { useForm } from "../../shared/hooks/form-hook";
import { useHttpClient } from "../../shared/hooks/http-hook";
import { AuthContext } from "../../shared/context/auth-context";

const Login = () => {
  const auth = useContext(AuthContext);
  //const [isLoginMode, setIsLoginMode] = useState(true);
  // const [isLoading, setIsLoading] = useState(false);
  // const [error, setError] = useState();
  const { isLoading, error, sendRequest, clearError } = useHttpClient(); //_____________

  const [formState, inputHandler] = useForm(
    {
      email: {
        value: "",
        isValid: false,
      },
      password: {
        value: "",
        isValid: false,
      },
    },
    false
  );

  // const switchModeHandler = () => {
  //   setIsLoginMode((prevMode) => !prevMode);
  // };

  const loginSubmitHandler = async (event) => {
    event.preventDefault();
    try{
      const responseData = await sendRequest(
            "http://localhost:5000/api/users/login",
            "POST",
            JSON.stringify({
              email: formState.inputs.email.value,
              password: formState.inputs.password.value
            }),
            {
              "Content-Type": "application/json",
            }
          );
          auth.login(responseData.userId, responseData.token);
    } catch (err) {

    }

    // try {
    //   setIsLoading(true);
    //   // instead of axios, fetch API, needs a string to the back-end route
    //   const response = await fetch("http://localhost:5000/api/users/login", {
    //     method: "POST",
    //     headers: {
    //       "Content-Type": "application/json",
    //     },
    //     body: JSON.stringify({
    //       email: formState.inputs.email.value,
    //       password: formState.inputs.password.value,
    //     }),
    //   });

    //   const responseData = await response.json();

    //   if (!response.ok) {
    //     throw new Error(responseData.error);
    //   }
    //   //console.log(responseData);
    //   setIsLoading(false);
    //   auth.login();

    // } catch (err) {
    //   //console.log(err);
    //   setIsLoading(false);
    //   setError(err.message || "Something went wrong, try again.");
    // }
  };

  // const errorHandler = () => {
  //   setError(null);
  // }

  return (
    <React.Fragment>
    <ErrorModal error={error} onClear={clearError}/>
    <Card className="authentication">
      {isLoading && <LoadingSpinner asOverlay />}
      <h2>Login Page</h2>
      <hr />
      <form onSubmit={loginSubmitHandler}>
        <Input
          id="email"
          element="input"
          type="email"
          label="Email"
          validators={[VALIDATOR_EMAIL()]}
          errorText="Please enter a valid email adress."
          onInput={inputHandler}
        />
        <Input
          id="password"
          element="input"
          type="password"
          label="Password"
          validators={[VALIDATOR_MINLENGTH(6), VALIDATOR_MAXLENGTH(30)]}
          errorText="Please enter a valid password (between 6 and 30 characters)."
          onInput={inputHandler}
        />

        <Button type="submit" disabled={!formState.isValid}>
          LOGIN
        </Button>
      </form>
      <Button inverse  to="/register">
        SWITCH TO REGISTER
      </Button>
    </Card>
    </React.Fragment>
  );
};

export default Login;
